import React, { useState, useEffect } from 'react';
import './App.css';
import axios from 'axios';

// 子页面组件
const SearchPage = ({ 
  API_BASE_URL, 
  query, 
  setQuery, 
  caseSensitive, 
  setCaseSensitive,
  searchResults,
  setSearchResults,
  loading,
  setLoading,
  apiStatus,
  error,
  setError,
  selectedResult,
  setSelectedResult,
  videoUrl,
  setVideoUrl,
  isVideoLoading,
  setIsVideoLoading,
  debugInfo,
  handleSearch,
  handleGenerateClip,
  formatTime,
  formatEpisode,
  highlightText
}) => {
  return (
    <>
      <section className="search-area">
        <form onSubmit={handleSearch}>
          <div className="search-box">
            <input
              type="text"
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              placeholder="输入要搜索的字幕内容..."
              disabled={apiStatus !== 'connected'}
            />
            <button 
              type="submit" 
              disabled={loading || apiStatus !== 'connected'}
            >
              {loading ? '搜索中...' : '搜索'}
            </button>
          </div>
          <div className="search-options">
            <label>
              <input
                type="checkbox"
                checked={caseSensitive}
                onChange={(e) => setCaseSensitive(e.target.checked)}
                disabled={apiStatus !== 'connected'}
              />
              区分大小写
            </label>
          </div>
        </form>

        {error && <div className="error-message">{error}</div>}
      </section>

      <div className="content-area">
        <section className="results-area">
          <h2>搜索结果 {searchResults.length > 0 && `(${searchResults.length})`}</h2>

          {loading ? (
            <div className="loading">搜索中，请稍候...</div>
          ) : (
            <ul className="results-list">
              {searchResults.map((result, index) => (
                <li 
                  key={index} 
                  className={selectedResult === result ? 'selected' : ''}
                  onClick={() => handleGenerateClip(result)}
                >
                  <div className="result-header">
                    <span className="episode">{formatEpisode(result.episode)}</span>
                    <span className="time">{formatTime(result.start_seconds)} - {formatTime(result.end_seconds)}</span>
                  </div>
                  <div 
                    className="subtitle-text"
                    dangerouslySetInnerHTML={{__html: highlightText(result.text, query)}}
                  />
                </li>
              ))}
            </ul>
          )}
        </section>

        <section className="video-area">
          <h2>视频播放</h2>
          {selectedResult ? (
            <div className="video-player">
              <div className="video-info">
                <p><strong>集数:</strong> {formatEpisode(selectedResult.episode)}</p>
                <p><strong>时间段:</strong> {formatTime(selectedResult.start_seconds)} - {formatTime(selectedResult.end_seconds)}</p>
                <p><strong>字幕内容:</strong> {selectedResult.text}</p>
              </div>

              {isVideoLoading ? (
                <div className="loading-video">生成视频片段中，请稍候...</div>
              ) : videoUrl ? (
                <video 
                  controls 
                  autoPlay 
                  className="video-element"
                  src={videoUrl}
                />
              ) : (
                <div className="video-placeholder">
                  {selectedResult ? '准备生成视频片段...' : '请从左侧选择一个搜索结果'}
                </div>
              )}
            </div>
          ) : (
            <div className="video-placeholder">
              请从左侧选择一个搜索结果
            </div>
          )}
        </section>
      </div>
    </>
  );
};

// 成语接龙游戏组件
const ChainGamePage = ({ 
  API_BASE_URL, 
  apiStatus,
  formatTime,
  formatEpisode
}) => {
  const [gameStarted, setGameStarted] = useState(false);
  const [currentPrompts, setCurrentPrompts] = useState([]);
  const [loadingPrompts, setLoadingPrompts] = useState(false);
  const [gameLog, setGameLog] = useState([]);
  const [currentSentence, setCurrentSentence] = useState(null);
  const [nextOptions, setNextOptions] = useState([]);
  const [loadingNextOptions, setLoadingNextOptions] = useState(false);
  const [gameError, setGameError] = useState(null);
  const [videoUrl, setVideoUrl] = useState('');
  const [isVideoLoading, setIsVideoLoading] = useState(false);
  const [selectedOption, setSelectedOption] = useState(null);
  const [isExportingVideo, setIsExportingVideo] = useState(false);
  const [clipUrls, setClipUrls] = useState([]);
  const [isConfirmMode, setIsConfirmMode] = useState(false);

  // 获取随机提示句子
  const fetchRandomPrompts = async () => {
    setLoadingPrompts(true);
    setGameError(null);

    try {
      const response = await axios.get(`${API_BASE_URL}/api/random_sentences`, {
        params: { count: 8 }
      });

      if (response.data && response.data.sentences) {
        setCurrentPrompts(response.data.sentences);
      } else {
        setGameError('获取提示句子失败');
      }
    } catch (error) {
      console.error('获取提示句子错误:', error);
      setGameError('无法获取提示句子，请确保API服务正常运行');
    } finally {
      setLoadingPrompts(false);
    }
  };

  // 刷新随机提示
  const refreshRandomPrompts = () => {
    if (apiStatus === 'connected' && !loadingPrompts) {
      fetchRandomPrompts();
    }
  };

  // 开始新游戏
  const startNewGame = () => {
    setGameLog([]);
    fetchRandomPrompts();
    setGameStarted(false);
    setCurrentSentence(null);
    setNextOptions([]);
    setClipUrls([]);
    setVideoUrl('');
    setSelectedOption(null);
    setIsConfirmMode(false);
  };

  // 生成视频片段
  const generateVideoClip = async (subtitle) => {
    setIsVideoLoading(true);
    setVideoUrl('');
    setGameError(null);

    try {
      const response = await axios.post(`${API_BASE_URL}/api/generate_clip`, {
        episode: subtitle.episode,
        start_time: subtitle.start_seconds,
        end_time: subtitle.end_seconds,
        context_seconds: 2
      });

      if (response.data.success) {
        setVideoUrl(`${API_BASE_URL}${response.data.video_url}`);
        return `${API_BASE_URL}${response.data.video_url}`;
      } else {
        setGameError('生成视频片段失败');
        return null;
      }
    } catch (error) {
      console.error('生成视频片段出错:', error);
      setGameError('视频服务出现错误，请确保视频文件存在且可访问');
      return null;
    } finally {
      setIsVideoLoading(false);
    }
  };

  // 选择开始句子
  const selectStartSentence = async (sentence) => {
    setSelectedOption(sentence);
    setIsConfirmMode(true);

    const clipUrl = await generateVideoClip(sentence);
    if (!clipUrl) return;
  };

  // 确认选择开始句子
  const confirmStartSentence = () => {
    const sentence = selectedOption;
    if (!sentence) return;

    setCurrentSentence(sentence);
    setGameLog([{...sentence, step: 1, clipUrl: videoUrl}]);
    setClipUrls([...clipUrls, videoUrl]);
    setGameStarted(true);
    fetchNextOptions(sentence.text);
    setIsConfirmMode(false);
    setSelectedOption(null);
  };

  // 取消选择
  const cancelSelection = () => {
    setIsConfirmMode(false);
    setSelectedOption(null);
    setVideoUrl('');
  };

  // 获取下一步选项
  const fetchNextOptions = async (text) => {
    if (!text) return;

    setLoadingNextOptions(true);
    setGameError(null);

    try {
      // 获取最后一个字，排除语气词
      let lastChar = getLastValidChar(text);

      if (!lastChar) {
        setGameError('无法识别有效的结尾字符');
        setLoadingNextOptions(false);
        return;
      }

      const response = await axios.get(`${API_BASE_URL}/api/search`, {
        params: {
          query: `^${lastChar}`,
          use_regex: true
        }
      });

      if (response.data && response.data.results) {
        // 过滤结果，保留3-8个字的句子
        const filteredResults = response.data.results.filter(item => {
          const cleanText = item.text.replace(/[啊呢吗吧呀嘛哦哎嗯呐呵呦诶哈哟]$/, '');
          return cleanText.length >= 3 && cleanText.length <= 8;
        });

        // 随机选择最多8个结果
        const shuffled = [...filteredResults].sort(() => 0.5 - Math.random());
        setNextOptions(shuffled.slice(0, 8));

        if (shuffled.length === 0) {
          setGameError('没有找到符合条件的接续句子');
        }
      } else {
        setGameError('获取接续句子失败');
      }
    } catch (error) {
      console.error('获取接续句子错误:', error);
      setGameError('无法获取接续句子，请确保API服务正常运行');
    } finally {
      setLoadingNextOptions(false);
    }
  };

  // 获取最后一个有效字符（排除语气词）
  const getLastValidChar = (text) => {
    if (!text || text.length === 0) return null;

    // 排除常见语气词
    const cleanText = text.replace(/[啊呢吗吧呀嘛哦哎嗯呐呵呦诶哈哟]$/, '');
    return cleanText.length > 0 ? cleanText[cleanText.length - 1] : null;
  };

  // 刷新下一步选项
  const refreshNextOptions = () => {
    if (currentSentence && !loadingNextOptions) {
      fetchNextOptions(currentSentence.text);
    }
  };

  // 选择下一个句子
  const selectNextSentence = async (sentence) => {
    setSelectedOption(sentence);
    setIsConfirmMode(true);

    const clipUrl = await generateVideoClip(sentence);
    if (!clipUrl) return;
  };

  // 确认选择下一个句子
  const confirmNextSentence = () => {
    const sentence = selectedOption;
    if (!sentence) return;

    setCurrentSentence(sentence);
    setGameLog([...gameLog, {...sentence, step: gameLog.length + 1, clipUrl: videoUrl}]);
    setClipUrls([...clipUrls, videoUrl]);
    fetchNextOptions(sentence.text);
    setIsConfirmMode(false);
    setSelectedOption(null);
  };

  // 回到上一步
  const goBackOneStep = () => {
    if (gameLog.length <= 1) {
      // 只有一步，回到选择开始句子
      setGameStarted(false);
      setGameLog([]);
      setCurrentSentence(null);
      setNextOptions([]);
      setClipUrls([]);
      return;
    }

    // 删除最后一步
    const newLog = gameLog.slice(0, -1);
    const newClipUrls = clipUrls.slice(0, -1);
    setGameLog(newLog);
    setClipUrls(newClipUrls);
    setCurrentSentence(newLog[newLog.length - 1]);
    fetchNextOptions(newLog[newLog.length - 1].text);
  };

  // 导出合并视频
  const exportMergedVideo = async () => {
    // 只从gameLog中获取选中的视频URL，而不是使用clipUrls
    const selectedClipUrls = gameLog.map(entry => entry.clipUrl).filter(url => url);

    if (selectedClipUrls.length === 0) {
      setGameError('没有可导出的视频');
      return;
    }

    setIsExportingVideo(true);
    setGameError(null);

    try {
      const response = await axios.post(`${API_BASE_URL}/api/merge_clips`, {
        clip_urls: selectedClipUrls
      });

      if (response.data.success) {
        // 触发下载
        const a = document.createElement('a');
        a.href = `${API_BASE_URL}${response.data.merged_video_url}`;
        a.download = '甄嬛传接龙.mp4';
        document.body.appendChild(a);
        a.click();
        document.body.removeChild(a);
      } else {
        setGameError('导出视频失败');
      }
    } catch (error) {
      console.error('导出视频错误:', error);
      setGameError('导出视频失败，请确保服务器正常运行');
    } finally {
      setIsExportingVideo(false);
    }
  };

  // 初始加载时获取随机提示
  useEffect(() => {
    if (apiStatus === 'connected') {
      fetchRandomPrompts();
    }
  }, [apiStatus]);

  return (
    <div className="chain-game-container">
      <div className="game-controls">
        <button 
          onClick={startNewGame}
          className="new-game-btn"
          disabled={apiStatus !== 'connected'}
        >
          新游戏
        </button>
        {gameStarted && (
          <button 
            onClick={goBackOneStep}
            className="back-btn"
            disabled={gameLog.length === 0}
          >
            上一步
          </button>
        )}
        {clipUrls.length > 1 && (
          <button
            onClick={exportMergedVideo}
            className="export-btn"
            disabled={isExportingVideo}
          >
            {isExportingVideo ? '导出中...' : '导出接龙视频'}
          </button>
        )}
      </div>

      {gameError && <div className="error-message">{gameError}</div>}

      <div className="game-content">
        {/* 左侧：候选词区域 */}
        <div className="game-candidates">
          {!gameStarted ? (
            <div className="start-game-section">
              <div className="section-header">
                <h2>选择开始句子</h2>
                <button 
                  onClick={refreshRandomPrompts}
                  className="refresh-btn"
                  disabled={loadingPrompts || apiStatus !== 'connected'}
                >
                  换一批
                </button>
              </div>

              {loadingPrompts ? (
                <div className="loading">加载提示句子中...</div>
              ) : (
                <ul className="prompts-list">
                  {currentPrompts.map((prompt, index) => (
                    <li 
                      key={index} 
                      onClick={() => selectStartSentence(prompt)}
                      className={selectedOption === prompt ? 'prompt-item selected' : 'prompt-item'}
                    >
                      <div className="prompt-text">{prompt.text}</div>
                      <div className="prompt-info">
                        <span className="episode">{formatEpisode(prompt.episode)}</span>
                        <span className="time">{formatTime(prompt.start_seconds)}</span>
                      </div>
                    </li>
                  ))}
                </ul>
              )}
              {currentPrompts.length === 0 && !loadingPrompts && (
                <div className="no-prompts">
                  <p>没有可用的提示句子</p>
                  <button 
                    onClick={fetchRandomPrompts}
                    disabled={apiStatus !== 'connected'}
                  >
                    重新获取
                  </button>
                </div>
              )}
            </div>
          ) : (
            <>
              <div className="current-section">
                <h2>当前句子</h2>
                <div className="current-sentence">
                  <div className="current-text">{currentSentence?.text}</div>
                  <div className="current-info">
                    <span className="episode">{formatEpisode(currentSentence?.episode)}</span>
                    <span className="time">{formatTime(currentSentence?.start_seconds)}</span>
                  </div>
                  <div className="next-hint">
                    下一句需以「{getLastValidChar(currentSentence?.text)}」字开头
                  </div>
                </div>
              </div>

              <div className="next-options-section">
                <div className="section-header">
                  <h2>可选接续</h2>
                  <button 
                    onClick={refreshNextOptions}
                    className="refresh-btn"
                    disabled={loadingNextOptions}
                  >
                    换一批
                  </button>
                </div>

                {loadingNextOptions ? (
                  <div className="loading">加载接续句子中...</div>
                ) : (
                  <ul className="options-list">
                    {nextOptions.map((option, index) => (
                      <li 
                        key={index}
                        onClick={() => selectNextSentence(option)}
                        className={selectedOption === option ? 'option-item selected' : 'option-item'}
                      >
                        <div className="option-text">{option.text}</div>
                        <div className="option-info">
                          <span className="episode">{formatEpisode(option.episode)}</span>
                          <span className="time">{formatTime(option.start_seconds)}</span>
                        </div>
                      </li>
                    ))}
                  </ul>
                )}
                {nextOptions.length === 0 && !loadingNextOptions && (
                  <div className="no-options">
                    <p>没有找到合适的接续句子</p>
                    <button onClick={startNewGame}>重新开始</button>
                  </div>
                )}
              </div>
            </>
          )}

          {/* 接龙记录 */}
          <div className="game-log-section">
            <h2>接龙记录</h2>
            {gameLog.length > 0 ? (
              <ol className="game-log-list">
                {gameLog.map((log, index) => (
                  <li key={index} className="log-item">
                    <div className="log-text">{log.text}</div>
                    <div className="log-info">
                      <span className="episode">{formatEpisode(log.episode)}</span>
                      <span className="time">{formatTime(log.start_seconds)}</span>
                      {log.clipUrl && (
                        <span className="clip-info">
                          <i className="video-icon">🎬</i>
                        </span>
                      )}
                    </div>
                  </li>
                ))}
              </ol>
            ) : (
              <div className="empty-log">尚未开始游戏</div>
            )}
          </div>
        </div>

        {/* 右侧：视频预览区域 */}
        <div className="game-video-preview">
          {isConfirmMode && (
            <div className="confirm-section">
              <div className="selected-sentence">
                <div className="selected-text">{selectedOption?.text}</div>
                <div className="selected-info">
                  <span className="episode">{formatEpisode(selectedOption?.episode)}</span>
                  <span className="time">{formatTime(selectedOption?.start_seconds)}</span>
                </div>
              </div>

              <div className="video-preview">
                {isVideoLoading ? (
                  <div className="loading-video">生成视频片段中，请稍候...</div>
                ) : videoUrl ? (
                  <video 
                    controls 
                    autoPlay 
                    className="video-element"
                    src={videoUrl}
                  />
                ) : (
                  <div className="video-placeholder">准备生成视频片段...</div>
                )}
              </div>

              <div className="confirm-actions">
                <button 
                  onClick={gameStarted ? confirmNextSentence : confirmStartSentence}
                  className="confirm-btn"
                  disabled={!videoUrl || isVideoLoading}
                >
                  确认选择
                </button>
                <button 
                  onClick={cancelSelection}
                  className="cancel-btn"
                >
                  取消选择
                </button>
              </div>
            </div>
          )}
          {!isConfirmMode && (
            <div className="video-placeholder large">
              <div className="placeholder-text">
                <p>请从左侧选择一个句子</p>
                <p>选择后将在此处预览视频</p>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

function App() {
  const [query, setQuery] = useState('');
  const [caseSensitive, setCaseSensitive] = useState(false);
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [selectedResult, setSelectedResult] = useState(null);
  const [videoUrl, setVideoUrl] = useState('');
  const [apiStatus, setApiStatus] = useState({loading: true, error: null, data: null});
  const [activePage, setActivePage] = useState('search'); // 'search' 或 'chain-game'

  // API 相关配置
  const API_BASE_URL = 'http://localhost:8089';

  // 检查API状态
  useEffect(() => {
    const checkApiStatus = async () => {
      try {
        const response = await fetch(`${API_BASE_URL}/api/status`);

        if (response.ok) {
          const data = await response.json();
          setApiStatus({loading: false, error: null, data});
          console.log("API状态:", data);
        } else {
          setApiStatus({loading: false, error: '无法连接到API服务', data: null});
        }
      } catch (error) {
        console.error("API连接错误:", error);
        setApiStatus({loading: false, error: '无法连接到API服务', data: null});
      }
    };

    checkApiStatus();
  }, []);

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!query.trim()) {
      setError('请输入搜索内容');
      return;
    }

    setLoading(true);
    setError('');
    setResults([]);
    setSelectedResult(null);
    setVideoUrl('');

    try {
      // 构建查询URL
      const queryParams = new URLSearchParams({
        query: query.trim(),
        case_sensitive: caseSensitive
      }).toString();

      const response = await fetch(`${API_BASE_URL}/api/search?${queryParams}`);

      if (response.ok) {
        const data = await response.json();
        setResults(data.results || []);
        if (data.results && data.results.length === 0) {
          setError('没有找到匹配的结果');
        }
      } else {
        const errorData = await response.json();
        setError(errorData.error || '搜索失败');
      }
    } catch (error) {
      console.error('搜索失败:', error);
      setError('搜索请求失败，请检查网络连接');
    } finally {
      setLoading(false);
    }
  };

  const handleGenerateClip = async (result) => {
    if (!result) return;

    setSelectedResult(result);
    setLoading(true);
    setError('');
    setVideoUrl('');

    try {
      const response = await fetch(`${API_BASE_URL}/api/generate_clip`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          episode: result.episode,
          start_time: result.start_time,
          end_time: result.end_time,
          context_seconds: 3 // 上下文秒数
        })
      });

      if (response.ok) {
        const data = await response.json();
        if (data.success) {
          // 确保视频URL包含完整的基础URL
          const fullVideoUrl = data.video_url.startsWith('http') 
            ? data.video_url 
            : `${API_BASE_URL}${data.video_url}`;
          setVideoUrl(fullVideoUrl);
        } else {
          setError(data.error || '视频片段生成失败');
        }
      } else {
        const errorData = await response.json();
        setError(errorData.error || '请求失败');
      }
    } catch (error) {
      console.error('生成视频片段失败:', error);
      setError('请求失败，请检查网络连接');
    } finally {
      setLoading(false);
    }
  };

  // 格式化时间显示
  const formatTime = (seconds) => {
    if (seconds === undefined || seconds === null) return '00:00:00';
    const hours = Math.floor(seconds / 3600);
    const mins = Math.floor((seconds % 3600) / 60);
    const secs = Math.floor(seconds % 60);
    return `${hours.toString().padStart(2, '0')}:${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  // 格式化集数显示
  const formatEpisode = (episode) => {
    if (!episode) return '';
    return episode.replace('后宫·甄嬛传', '第') + '集';
  };

  // 高亮显示匹配文本
  const highlightText = (text, query) => {
    if (!query || !text) return text;

    try {
      const regex = new RegExp(query, caseSensitive ? 'g' : 'gi');
      return text.replace(regex, match => `<mark>${match}</mark>`);
    } catch (e) {
      return text;
    }
  };

  // 切换页面
  const switchPage = (page) => {
    setActivePage(page);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>《后宫·甄嬛传》字幕搜索系统</h1>
        {apiStatus.loading && <p className="api-status checking">检查API连接中...</p>}
        {apiStatus.error && <p className="api-status error">无法连接到API服务，请确保后端服务已启动</p>}
        {apiStatus.data && <p className="api-status connected">API服务已连接</p>}
        <div className="debug-info">{apiStatus.data ? JSON.stringify(apiStatus.data) : apiStatus.error}</div>

        <nav className="main-nav">
          <ul>
            <li className={activePage === 'search' ? 'active' : ''}>
              <button onClick={() => switchPage('search')}>字幕搜索</button>
            </li>
            <li className={activePage === 'chain-game' ? 'active' : ''}>
              <button onClick={() => switchPage('chain-game')}>成语接龙</button>
            </li>
          </ul>
        </nav>
      </header>

      <main className="App-main">
        {activePage === 'search' ? (
          <SearchPage
            API_BASE_URL={API_BASE_URL}
            query={query}
            setQuery={setQuery}
            caseSensitive={caseSensitive}
            setCaseSensitive={setCaseSensitive}
            searchResults={results}
            setSearchResults={setResults}
            loading={loading}
            setLoading={setLoading}
            apiStatus={apiStatus.data ? 'connected' : 'error'}
            error={error}
            setError={setError}
            selectedResult={selectedResult}
            setSelectedResult={setSelectedResult}
            videoUrl={videoUrl}
            setVideoUrl={setVideoUrl}
            isVideoLoading={loading}
            setIsVideoLoading={setLoading}
            debugInfo={apiStatus.data ? JSON.stringify(apiStatus.data) : apiStatus.error}
            handleSearch={handleSearch}
            handleGenerateClip={handleGenerateClip}
            formatTime={formatTime}
            formatEpisode={formatEpisode}
            highlightText={highlightText}
          />
        ) : (
          <ChainGamePage
            API_BASE_URL={API_BASE_URL}
            apiStatus={apiStatus.data ? 'connected' : 'error'}
            formatTime={formatTime}
            formatEpisode={formatEpisode}
          />
        )}
      </main>

      <footer className="App-footer">
        <p>《后宫·甄嬛传》字幕搜索系统 ©2024</p>
      </footer>
    </div>
  );
}

export default App;
