import React, { useState, useEffect } from 'react';
import './App.css';
import axios from 'axios';

// å­é¡µé¢ç»„ä»¶
const SearchPage = ({ 
  API_BASE_URL, 
  query, 
  setQuery, 
  caseSensitive, 
  setCaseSensitive,
  searchResults,
  setSearchResults,
  loading,
  setLoading,
  apiStatus,
  error,
  setError,
  selectedResult,
  setSelectedResult,
  videoUrl,
  setVideoUrl,
  isVideoLoading,
  setIsVideoLoading,
  debugInfo,
  handleSearch,
  handleGenerateClip,
  formatTime,
  formatEpisode,
  highlightText
}) => {
  return (
    <>
      <section className="search-area">
        <form onSubmit={handleSearch}>
          <div className="search-box">
            <input
              type="text"
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              placeholder="è¾“å…¥è¦æœç´¢çš„å­—å¹•å†…å®¹..."
              disabled={apiStatus !== 'connected'}
            />
            <button 
              type="submit" 
              disabled={loading || apiStatus !== 'connected'}
            >
              {loading ? 'æœç´¢ä¸­...' : 'æœç´¢'}
            </button>
          </div>
          <div className="search-options">
            <label>
              <input
                type="checkbox"
                checked={caseSensitive}
                onChange={(e) => setCaseSensitive(e.target.checked)}
                disabled={apiStatus !== 'connected'}
              />
              åŒºåˆ†å¤§å°å†™
